import React, { useState } from 'react';
import axios from 'axios';

function NewReminderForm({ onNewReminder, onClose }) {
  const [formData, setFormData] = useState({
    event_title: '',
    event_description: '',
    event_date: '',
    event_time: '',
    event_category: 'Work' // Default category
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/reminders/', formData);
      onNewReminder(response.data); // Pass the new reminder data to the parent component
      // Clear form data after successful submission
      setFormData({
        event_title: '',
        event_description: '',
        event_date: '',
        event_time: '',
        event_category: 'Work' // Reset category to default
      });
    } catch (error) {
      console.error('Error adding new reminder:', error);
    }
  };

  return (
    <div style={{ backgroundColor: '#f9f9f9', padding: '20px', borderRadius: '5px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)', width: '30%' }}>
      <h2 style={{ marginBottom: '20px' }}>New Reminder</h2>
      <button onClick={onClose} style={{ position: 'absolute', top: '10px', right: '10px', backgroundColor: '#f44336', color: '#fff', border: 'none', borderRadius: '50%', width: '30px', height: '30px', cursor: 'pointer', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>X</button>
      <form onSubmit={handleSubmit} style={{ display: 'grid', gridGap: '10px' }}>
        <div style={{ display: 'grid', gridTemplateColumns: 'auto auto', gridGap: '10px' }}>
          <label style={{ width: '100px' }}>Title:</label>
          <input type="text" name="event_title" value={formData.event_title} onChange={handleInputChange} style={{ width: 'calc(100% - 100px)', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        </div>
        {/* Other input fields */}
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', cursor: 'pointer' }}>Add Reminder</button>
          <button type="button" onClick={onClose} style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#f44336', color: '#fff', border: 'none', cursor: 'pointer' }}>Close</button>
        </div>
      </form>
    </div>
  );
}

export default NewReminderForm;
