import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EditReminder from './EditReminder';
import Reminders from './Reminders';

function ReminderDetail({ reminderId, onDeleteSuccess }) {
    const [reminder, setReminder] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [deletionSuccess, setDeletionSuccess] = useState(false); // State to track deletion success
    const [showEditModal, setShowEditModal] = useState(false);
    const [showReminders, setShowReminders] = useState(true); // State to control rendering of Reminders component

    useEffect(() => {
        const fetchReminder = async () => {
            try {
                const storedToken = localStorage.getItem('token');
                if (storedToken) {
                    const config = {
                        headers: {
                            Authorization: `Bearer ${storedToken}`
                        }
                    };

                    const response = await axios.get(`http://localhost:8000/reminders/${reminderId}/?format=json`, config);
                    setReminder(response.data);
                }
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };

        fetchReminder();
    }, [reminderId]);

    const handleEdit = () => {
        setShowEditModal(true);
    };

    const handleEditSuccess = () => {
        // Refresh reminder data after editing
        // Re-fetching the reminder data can be done here if needed
        // Close the edit modal
        setShowEditModal(false);
        setShowReminders(true); // Render Reminders component after successful edit
    };

    const handleDelete = async () => {
        try {
            const storedToken = localStorage.getItem('token');
            if (storedToken) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${storedToken}`
                    }
                };

                await axios.delete(`http://localhost:8000/reminders/${reminderId}/`, config);
                window.alert('Reminder deleted successfully.');
                // Update deletion success state
                setDeletionSuccess(true);
                onDeleteSuccess(); // Notify parent component about deletion success
            }
        } catch (error) {
            // Handle delete error (e.g., show error message)
            console.error('Delete error:', error);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            {showReminders ? (
                <Reminders />
            ) : (
                <EditReminder reminder={reminder} onUpdateSuccess={handleEditSuccess} onClose={() => setShowEditModal(false)} />
            )}
            <div style={{ margin: 50 }}>
                <button
                    onClick={handleEdit}
                    style={{
                        backgroundColor: '#008CBA', // Edit button color
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        marginRight: '10px', // Add margin-right for spacing
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Add box shadow for depth
                        transition: 'background-color 0.3s ease', // Add transition for smooth color change
                        outline: 'none', // Remove outline on focus
                    }}
                >
                    Edit
                </button>
                <button
                    onClick={handleDelete}
                    style={{
                        backgroundColor: '#f44336', // Delete button color
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Add box shadow for depth
                        transition: 'background-color 0.3s ease', // Add transition for smooth color change
                        outline: 'none', // Remove outline on focus
                    }}
                >
                    Delete
                </button>
            </div>
            <div style={{ margin: 50 }}>
                <p>
                    <strong>Title:</strong> {reminder.event_title}
                </p>
                <p>
                    <strong>Description:</strong> {reminder.event_description}
                </p>
                <p>
                    <strong>Date:</strong> {reminder.event_date}
                </p>
                <p>
                    <strong>Time:</strong> {reminder.event_time}
                </p>
                <p>
                    <strong>Category:</strong> {reminder.event_category}
                </p>
            </div>
        </div>
    );
}

export default ReminderDetail;
