import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReminderDetail from './ReminderDetail';

function Reminders({ onReminderSelect }) {
  const [reminders, setReminders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deletionSuccess, setDeletionSuccess] = useState(false); // State to track deletion success

  useEffect(() => {
    const fetchReminders = async () => {
      try {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
          const config = {
            headers: {
              Authorization: `Bearer ${storedToken}`
            }
          };

          const response = await axios.get('http://localhost:8000/reminders/', config);
          setReminders(response.data);
        }
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchReminders();
  }, [deletionSuccess]); // Update reminders list when deletion success changes

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2 style={{ backgroundColor: '#4CAF50', color: '#fff', padding: '10px' }}>Reminders</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px', padding: '20px' }}>
        {reminders.map((reminder) => (
          <ReminderDetail
            key={reminder.id}
            reminderId={reminder.id}
            onBack={() => {}}
            onDeleteSuccess={() => setDeletionSuccess(true)} // Set deletion success to true when a reminder is deleted
          />
        ))}
      </div>
      {deletionSuccess && <p style={{ color: 'green' }}>Reminder deleted successfully.</p>} {/* Display success message */}
    </div>
  );
}

export default Reminders;
