import React, { useState } from 'react';
import axios from 'axios';

function NewReminderForm({ onNewReminder }) {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    time: '',
    category: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/reminders/', formData);
      onNewReminder(response.data); // Pass the new reminder data to the parent component
      // Clear form data after successful submission
      setFormData({
        title: '',
        description: '',
        date: '',
        time: '',
        category: ''
      });
    } catch (error) {
      console.error('Error adding new reminder:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Title:
        <input type="text" name="title" value={formData.title} onChange={handleChange} />
      </label>
      <label>
        Description:
        <textarea name="description" value={formData.description} onChange={handleChange}></textarea>
      </label>
      <label>
        Date:
        <input type="date" name="date" value={formData.date} onChange={handleChange} />
      </label>
      <label>
        Time:
        <input type="time" name="time" value={formData.time} onChange={handleChange} />
      </label>
      <label>
        Category:
        <input type="text" name="category" value={formData.category} onChange={handleChange} />
      </label>
      <button type="submit">Add Reminder</button>
    </form>
  );
}

export default NewReminderForm;
