import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function ReminderDetail({ reminderId }) {
  const [reminder, setReminder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const fetchReminder = async () => {
      try {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
          const config = {
            headers: {
              Authorization: `Bearer ${storedToken}`
            }
          };

          const response = await axios.get(`http://localhost:8000/reminders/${reminderId}/?format=json`, config);
          setReminder(response.data);
        }
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchReminder();
  }, [reminderId]);

  const handleDelete = async () => {
    try {
      const storedToken = localStorage.getItem('token');
      if (storedToken) {
        const config = {
          headers: {
            Authorization: `Bearer ${storedToken}`
          }
        };

        await axios.delete(`http://localhost:8000/reminders/${reminderId}/`, config);
        history.push('/'); // Redirect to the home page after deletion
      }
    } catch (error) {
      setError(error.message);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={{ backgroundColor: '#f9f9f9', padding: '20px', borderRadius: '10px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
      <h2 style={{ marginBottom: '15px', color: '#333' }}>Reminder Details</h2>
      <div>
        <p><strong>Title:</strong> {reminder.event_title}</p>
        <p><strong>Description:</strong> {reminder.event_description}</p>
        <p><strong>Date:</strong> {reminder.event_date}</p>
        <p><strong>Time:</strong> {reminder.event_time}</p>
        <p><strong>Category:</strong> {reminder.event_category}</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <button onClick={() => history.push('/')} style={{ marginRight: '10px' }}>Back</button>
        <button onClick={handleDelete} style={{ backgroundColor: 'red', color: '#fff' }}>Delete</button>
      </div>
    </div>
  );
}

export default ReminderDetail;
