// EditReminder.js

import React, { useState } from 'react';
import axios from 'axios';

function EditReminder({ reminder, onUpdateSuccess, onClose }) {
  const [updatedReminder, setUpdatedReminder] = useState(reminder);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedReminder({ ...updatedReminder, [name]: value });
  };

  const handleUpdate = async () => {
    try {
      const storedToken = localStorage.getItem('token');
      if (storedToken) {
        const config = {
          headers: {
            Authorization: `Bearer ${storedToken}`
          }
        };

        await axios.put(`http://localhost:8000/reminders/${reminder.id}/`, updatedReminder, config);

        // Notify parent component about update success
        onUpdateSuccess();

        // Close the edit modal
        onClose();
      }
    } catch (error) {
      // Handle update error (e.g., show error message)
      console.error('Update error:', error);
    }
  };

  return (
    <div>
      <h2>Edit Reminder</h2>
      <form>
        <div>
          <label>Title:</label>
          <input type="text" name="event_title" value={updatedReminder.event_title} onChange={handleInputChange} />
        </div>
        <div>
          <label>Description:</label>
          <textarea name="event_description" value={updatedReminder.event_description} onChange={handleInputChange} />
        </div>
        <div>
          <label>Date:</label>
          <input type="date" name="event_date" value={updatedReminder.event_date} onChange={handleInputChange} />
        </div>
        <div>
          <label>Time:</label>
          <input type="time" name="event_time" value={updatedReminder.event_time} onChange={handleInputChange} />
        </div>
        <div>
          <label>Category:</label>
          <select name="event_category" value={updatedReminder.event_category} onChange={handleInputChange}>
            <option value="Work">Work</option>
            <option value="Personal">Personal</option>
            <option value="Health">Health</option>
          </select>
        </div>
        <button type="button" onClick={handleUpdate}>Update</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
}

export default EditReminder;
