import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ReminderDetail({ reminderId, onBack }) {
    const [reminder, setReminder] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReminder = async () => {
            try {
                const storedToken = localStorage.getItem('token');
                if (storedToken) {
                    const config = {
                        headers: {
                            Authorization: `Bearer ${storedToken}`
                        }
                    };

                    const response = await axios.get(`http://localhost:8000/reminders/${reminderId}/?format=json`, config);
                    setReminder(response.data);
                }
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };

        fetchReminder();
    }, [reminderId]);

    const handleEdit = () => {
        // Handle edit action
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h2 style={{ backgroundColor: '#4CAF50', color: '#fff', padding: '10px' }}>Reminders Detailes:</h2>
            <h2>Reminder Details</h2>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px', padding: '20px' }}>
                <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '20px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>

                    <p><strong>Title:</strong> {reminder.event_title}</p>
                    <p><strong>Description:</strong> {reminder.event_description}</p>
                    <p><strong>Date:</strong> {reminder.event_date}</p>
                    <p><strong>Time:</strong> {reminder.event_time}</p>
                    <p><strong>Category:</strong> {reminder.event_category}</p>
                </div>
            </div>
            <div>
                <button
                    onClick={onBack}
                    style={{
                        backgroundColor: '#4CAF50',
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        marginRight: '10px' // Add margin-right for spacing
                    }}
                >
                    Back
                </button>
                <button
                    onClick={handleEdit}
                    style={{
                        backgroundColor: '#008CBA', // Edit button color
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        fontSize: '16px'
                    }}
                >
                    Edit
                </button>
            </div>
        </div>
    );
}

export default ReminderDetail;
