import React, { useState } from 'react';
import LoginForm from './components/Loginform';
import Reminders from './components/Reminders';
import ReminderDetail from './components/ReminderDetail'; // Assuming you'll create a new component for reminder detail

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showReminderDetail, setShowReminderDetail] = useState(false);
  const [selectedReminderId, setSelectedReminderId] = useState(null);

  // Callback function to handle login success
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
  };

  // Function to show reminder detail
  const showDetail = (id) => {
    setSelectedReminderId(id);
    setShowReminderDetail(true);
  };

  // Function to hide reminder detail
  const hideDetail = () => {
    setSelectedReminderId(null);
    setShowReminderDetail(false);
  };

  return (
    <div>
      {isLoggedIn ? (
        <div>
          {!showReminderDetail ? (
            <Reminders showDetail={showDetail} />
          ) : (
            <ReminderDetail id={selectedReminderId} hideDetail={hideDetail} />
          )}
        </div>
      ) : (
        <LoginForm onLoginSuccess={handleLoginSuccess} />
      )}
    </div>
  );
}

export default App;
